#include <vector>
#include <iostream>
#include <algorithm>
#include <set>
using namespace std;

class Solution {
public:
	bool isSubsequence(string s, string t) {
		int cur = -1;
		for (auto ss : s)
		{
			auto index = t.find(ss, cur + 1);
			if (index == string::npos)
			{
				return false;
			}
			cur = index;

		}
		return true;
	}
};

int main()
{
	Solution s;
	s.isSubsequence("leeeeetcode","");
	return getchar();
}